import {useState} from 'react'
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button'
import axios from 'axios'

export default function User_login() {
  const[userInfo, setuserInfo]=useStat({
    Email:"",
    Password:"",
  })


  function UserInput (event){
    const{name,value}=event.target
    setuserInfo({...userInfo, [name]:value})
  }

  async function handleSubmit(event){
    const response = await axios.post("http:/localhost:4000/users/", userInfo)
    console.log(response)
  }
  return (
    <>
    <Form>
       <Form.Group as={Row} className="mb-3" controlId="formPlaintextPassword">
        <Col sm="10">
          <Form.Control type="password" placeholder="Email"  onChange={(event)=> userInput(event)}/>
        </Col>
      </Form.Group>

      <Form.Group as={Row} className="mb-3" controlId="formPlaintextPassword">
        <Col sm="10">
          <Form.Control type="password" placeholder="Password" />
        </Col>
      </Form.Group>
    </Form>

    <Button variant="dark" onClick={handleSubmit}>Login</Button>

    
    
    
    </>
  )
}
