import {useEffect, useState} from 'react'
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import axios from "axios"

export default function AddingQuestions({categoryID,fetchResponse}) {
console.log(categoryID)
  const[userQ, setuserQ]=useState({
    Q:"", 
    A:""
})


  function UserInput (event){
    const{name,value}= (event.target)
    setuserQ({...userQ, [name]:value})
  }

  async function handleSubmit(event){
    try{
      const response = await axios.post("http://localhost:4000/questions/", {Q: userQ.Q, A: userQ.A, categoryID:categoryID})
      console.log(response)
      fetchResponse()
    }
    catch (error) {
      console.log("this is an error",error)
    }
  }

  return (
    <>


    <Form>
    <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Question</Form.Label>
        <Form.Control as="textarea" type="email" placeholder="Question" name="Q" onChange={(event) => UserInput(event)}/>
    </Form.Group>
    <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Answer</Form.Label>
        <Form.Control as="textarea" rows={3} name="A" onChange={(event) => UserInput(event)}/>
    </Form.Group>
    </Form>

    <Button variant="dark" onClick={handleSubmit}>Add Q & A</Button>
    </>
  )
}
